mapper.update:
update demo_name set name = #{name,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP}, version = #{version,jdbcType=BIGINT} +1
id = #{id,jdbcType=INTEGER}
and version = #{version,jdbcType=BIGINT}

mapper.select:
select 

from 
demo_name


    and id = #{condition.id,jdbcType=INTEGER}


    and name = #{condition.name,jdbcType=VARCHAR}


    and create_time = #{condition.createTime,jdbcType=TIMESTAMP}


    and update_time = #{condition.updateTime,jdbcType=TIMESTAMP}


    and version = #{condition.version,jdbcType=BIGINT}



  ORDER BY ${page.sort} ${page.order}


  limit #{page.offset}, #{page.limit}

select count(*) from 
demo_name


    and id = #{condition.id,jdbcType=INTEGER}


    and name = #{condition.name,jdbcType=VARCHAR}


    and create_time = #{condition.createTime,jdbcType=TIMESTAMP}


    and update_time = #{condition.updateTime,jdbcType=TIMESTAMP}


    and version = #{condition.version,jdbcType=BIGINT}
